import './App.css';
import {Badge, Button, Col, Layout, Menu, Popover, Row, Typography} from "antd";
import {ShoppingCartOutlined} from "@ant-design/icons";
import Product from "./components/Product/Product"
import {useEffect, useState} from "react";

const {Header, Sider, Content} = Layout;
const {SubMenu} = Menu

const data = [
    {
        name: "Macbook Pro",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Magic Keyboard",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Ipad Pro",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Ipad Air",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Magic Mouse",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Macbook Air",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Iphone 13",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Iphone 13 case",
        image: "data:image/jpeg;base64,"
    },
    {
        name: "Ipad Pro case",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSiSE6Iul9UHvI0NNuthK6chRR9hgUcIYxWQQ&usqp=CAU"
    }

]

function getProducts() {
    return data;
}

function App() {
    const [products, setProducts] = useState([]);
    const [cartProducts, setCartProducts] = useState([]);

    // Similar to componentDidMount and componentDidUpdate:
    useEffect(() => {
        // Update the document title using the browser API
        let prods = getProducts()
        // prods.forEach(prod => prod.isSelected = false)
        setProducts(prods)
    },[]);

    function addToCart(product) {
        let prods = cartProducts.slice()
        prods.push(product)
        setCartProducts(prods)
    }

    function removeFromCart(product) {
        let prods = cartProducts.slice()
        var index = prods.indexOf(product);
        if (index > -1) {
            prods.splice(index, 1);
        }
        setCartProducts(prods)
    }

    return (
        <Layout className={"fullHeight"}>
            <Header>
                <div style={{display: "flex"}}>
                    <h2 style={{color: "white"}}>Group Academy Store</h2>
                    <div style={{marginLeft: 'auto'}}>
                        <Popover
                            content={cartProducts.map(product =>
                                        <Product product={product} isCartView={true} click={removeFromCart}/>
                                )}
                            title="Shopping cart"
                            trigger="click"
                        >
                            <Badge count={cartProducts.length}>
                                <Button icon>
                                    <ShoppingCartOutlined/>
                                </Button>
                            </Badge>
                        </Popover>
                    </div>
                </div>
            </Header>
            <Layout>
                <Sider style={{background: "white"}}>
                    <Menu mode="inline">
                        <Menu.Item>Apple</Menu.Item>
                        <SubMenu title="Accessories">
                            <Menu.Item>Keyboards</Menu.Item>
                            <Menu.Item>Mouses</Menu.Item>
                        </SubMenu>
                    </Menu>
                </Sider>
                <Layout>
                    <Content>
                        <Row style={{margin: "8px"}} gutter={[16, 16]}>
                            {products.map(product =>
                                <Col span={8}>
                                    <Product product={product} isCartView={false} click={addToCart}/>
                                </Col>
                            )}
                        </Row>
                    </Content>
                </Layout>
            </Layout>
        </Layout>
    );
}

export default App;
